mapper.select:
SELECT
	*
FROM member 
WHERE 1=1

	AND resignNy = #{shDelNy}



		AND id LIKE CONCAT('%', #{shValue},'%') 
		AND mobile = right(#{shValue},4) 
		AND name LIKE CONCAT('%', #{shValue},'%')


		AND joinDate BETWEEN #{ShDateStart} AND #{shDateEnd}
		AND dob BETWEEN #{ShDateStart} AND #{shDateEnd}

mapper.insert:
	INSERT INTO member(
		id
		,name
		,mobile
		,email
		,dob
	)
	VALUES(
		#{id}
		,#{name}
		,#{mobile}
		,#{email}
		,#{dob}
	)


SELECT last_insert_id()
